import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { View, Text } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import { SafeAreaView } from 'react-native-safe-area-context';

export default function Temas() {
    const [temas, setTemas] = useState([]);

    useEffect(() => {
        axios
          .get(
            "https://ijsv-backend.herokuapp.com/api/temas"
          )
          .then((response) => {
            setTemas(response.data);
          })
          .catch((err) => {
            console.log(err);
          });
      }, []);
          if (!temas) {
              return <Text style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>Loading...</Text>;
    }
    
    return (
        <SafeAreaView>
            <ScrollView>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Text>Temas </Text>
                </View>
                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', font:24 }}>
                {temas.map(themes => {
                    return (
                        <View key={themes.id}>
                            <Text style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>{themes.title}</Text>
                            <Text style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>{themes.body1}</Text>
                            <Text style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>{themes.body2}</Text>
                            <Text style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>{themes.body3}</Text>
            </View>
                    )
                })}
                    </View>
                </ScrollView>
            </SafeAreaView>
    );
  }
